import { SET_USER } from "../action";

const initialState = {
   data: JSON.parse()
}

const getlocalStorage=(data=[])=>{
    localStorage.setItem('data',data)

}

export const setUsers = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER:
            return{
                    ...state,
                    userInfo: action.payload,
            }
            default:
            return  state
    }
}
